import {createStore} from 'vuex'

// 모듈 시스템을 사용하는 경우 Vue.use(Vuex)를 먼저 호출해야합니다.

const store = createStore({
    state() {
        return {
            account: {
                id: 0
            }

        }
    },
    mutations: {
        setAccount(state, payload) {
            state.account.id = payload;
        }
    }
})


export default store;
